  }, []);
  return (
    <div
      style={{
        width: "100vw",
        height: "100vh",
        display: "flex",
        flexDirection: "column",
        alignItems: "center",
        background: "linear-gradient(to right top,cadetblue,lavenderblush)",
      }}
    >
      {/* <div style={{ display: "flex", justifyContent: "center" , flex: }}> */}
      <Typography
        style={{
          fontSize: "2rem",
          fontFamily: "gillsans",
          marginTop: "10%",
          color: "crimson",
        }}
      >
        TODOS
      </Typography>
      <FormControl>
        <InputLabel>What needs to be done?</InputLabel>
        <OutlinedInput
          id="outlined-basic"
          label="What needs to be done?"
          // variant="outlined"
          style={{ marginTop: "1%", width: "20vw" }}
          onChange={(e) => setTodo(e.target.value)}
          endAdornment={
            <InputAdornment position="end">
              <IconButton
                aria-label="toggle password visibility"
                //   onClick={handleClickShowPassword}
                //   onMouseDown={handleMouseDownPassword}
                edge="end"
              >
                <Visibility />
              </IconButton>
            </InputAdornment>
          }
        />
      </FormControl>
      {/* </div> */}
    </div>
  );
}
