import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import {
  Button,
  Stack,
  IconButton,
  Divider,
  Card,
  TextField,
  Typography,
  InputAdornment,
  OutlinedInput,
  FormControl,
  InputLabel,
} from "@mui/material";
import {
  Visibility,
  VisibilityOff,
  DirectionsCar,
  BarChart,
  AttachMoney,
  Email,
  Apartment,
  DateRange,
} from "@mui/icons-material";
import { useQuery, gql } from "@apollo/client";

const GET_USERS = gql`
  query GetUsers {
    users {
      id
      name
    }
  }
`;

const GET_USER = gql`
  query MyQuery($name: String!) {
    users(where: { name: { _eq: $name } }) {
      name
      id
    }
  }
`;

export default function Main({ name }) {
  const [todo, setTodo] = useState("");
  const [users, setUsers] = useState([]);
  //   const { loading, error, data } = useQuery(GET_USERS);
  const { loading, error, data } = useQuery(GET_USER, {
    variables: { name: "alex" },
  });

  const [values, setValues] = React.useState({
    amount: "",
    password: "",
    weight: "",
    weightRange: "",
    showPassword: false,
  });

  const handleChange = (prop) => (event) => {
    setValues({ ...values, [prop]: event.target.value });
  };

  const handleClickShowPassword = () => {
    setValues({
      ...values,
      showPassword: !values.showPassword,
    });
  };

  const handleMouseDownPassword = (event) => {
    event.preventDefault();
  };

  let navigate = useNavigate();

  useEffect(() => {
    console.log(users);
  }, [users]);

  useEffect(() => {
    if (data) {
      setUsers(data.users);
    }
  }, [data]);

  return (
    <div
      style={{
        width: "100vw",
        height: "100vh",
        display: "flex",
        flexDirection: "column",
        alignItems: "center",
        background: "linear-gradient(to right top,ghostwhite,floralwhite)",
      }}
    >
      {/* <div style={{ display: "flex", justifyContent: "center" , flex: }}> */}
      <Typography
        style={{
          fontSize: "4rem",
          fontFamily: "gillsans",
          marginTop: "10%",
          color: "crimson",
        }}
      >
        TODOS
      </Typography>
      <FormControl>
        <InputLabel>What needs to be done?</InputLabel>
        <OutlinedInput
          id="outlined-basic"
          label="What needs to be done?"
          // variant="outlined"
          style={{ marginTop: "1%", width: "20vw" }}
          onChange={(e) => setTodo(e.target.value)}
          endAdornment={
            <InputAdornment position="end">
              <IconButton
                aria-label="toggle password visibility"
                //   onClick={handleClickShowPassword}
                //   onMouseDown={handleMouseDownPassword}
                edge="end"
              >
                <Visibility />
              </IconButton>
            </InputAdornment>
          }
        />
      </FormControl>
      {/* </div> */}
      <FormControl sx={{ m: 1, width: "25ch" }} variant="outlined">
        <InputLabel htmlFor="outlined-adornment-password">Password</InputLabel>
        <OutlinedInput
          id="outlined-adornment-password"
          type={values.showPassword ? "text" : "password"}
          value={values.password}
          onChange={handleChange("password")}
          endAdornment={
            <InputAdornment position="end">
              <IconButton
                aria-label="toggle password visibility"
                onClick={handleClickShowPassword}
                onMouseDown={handleMouseDownPassword}
                edge="end"
              >
                {values.showPassword ? <VisibilityOff /> : <Visibility />}
              </IconButton>
            </InputAdornment>
          }
          label="Password"
        />
      </FormControl>
    </div>
  );
}
