import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import {
  Button,
  Stack,
  IconButton,
  Divider,
  Box,
  Paper,
  Card,
  TextField,
  Typography,
  InputAdornment,
  OutlinedInput,
  FormControl,
  InputLabel,
  FilledInput,
} from "@mui/material";
import {
  Visibility,
  KeyboardArrowDown,
  DirectionsCar,
  BarChart,
  AttachMoney,
  Email,
  Apartment,
  DateRange,
} from "@mui/icons-material";
import { useQuery, gql } from "@apollo/client";

const GET_USERS = gql`
  query GetUsers {
    users {
      id
      name
    }
  }
`;

const GET_USER = gql`
  query MyQuery($name: String!) {
    users(where: { name: { _eq: $name } }) {
      name
      id
    }
  }
`;

export default function Main({ name }) {
  const [todo, setTodo] = useState([]);
  const [users, setUsers] = useState([]);
  //   const { loading, error, data } = useQuery(GET_USERS);
  const { loading, error, data } = useQuery(GET_USER, {
    variables: { name: "alex" },
  });

  let navigate = useNavigate();

  useEffect(() => {
    console.log(users);
  }, [users]);

  useEffect(() => {
    if (data) {
      setUsers(data.users);
    }
  }, [data]);

  return (
    <div
      style={{
        width: "100vw",
        height: "100vh",
        display: "flex",
        flexDirection: "column",
        alignItems: "center",
        background: "linear-gradient(to right top,ghostwhite,floralwhite)",
      }}
    >
      {/* <div style={{ display: "flex", justifyContent: "center" , flex: }}> */}
      <Typography
        style={{
          fontSize: "4rem",
          fontFamily: "gillsans",
          marginTop: "10%",
          color: "crimson",
        }}
      >
        TODOS
      </Typography>
      <FormControl sx={{ m: 1, width: "20vw" }} variant="filled">
        <InputLabel htmlFor="filled-adornment-password">
          What needs to be done?
        </InputLabel>
        <FilledInput
          id="filled-adornment-password"
        //   value={todo}
          //   label="What needs to be done?"
          // variant="outlined"
        //   onChange={(e) => setTodo((p) => [...p, e.target.value])}
          onKeyDown={(e) => {
            if (e.key === "Enter") {
              console.log("enter");
              setTodo((p) => [...p, e.target.value]);
              e.t
            }
          }}
          endAdornment={
            <InputAdornment position="end">
              <IconButton
                aria-label="toggle password visibility"
                //   onClick={handleClickShowPassword}
                //   onMouseDown={handleMouseDownPassword}
                edge="end"
              >
                <KeyboardArrowDown />
              </IconButton>
            </InputAdornment>
          }
        />
      </FormControl>
      {/* </div> */}
      <Box
        sx={{
          display: "flex",
          flexWrap: "wrap",
          "& > :not(style)": {
            m: 1,
            width: 128,
            height: 128,
          },
        }}
      >
        <Paper elevation={3}>{todo}</Paper>
      </Box>
    </div>
  );
}
