import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import {
  Button,
  Stack,
  IconButton,
  Divider,
  Box,
  Paper,
  CardContent,
  CardActions,
  Card,
  TextField,
  Typography,
  InputAdornment,
  OutlinedInput,
  FormControl,
  InputLabel,
  FilledInput,
  List,
  ListItemText,
} from "@mui/material";
import {
  Visibility,
  KeyboardArrowDown,
  DirectionsCar,
  BarChart,
  AttachMoney,
  Email,
  Apartment,
  DateRange,
} from "@mui/icons-material";
import { useQuery, gql } from "@apollo/client";

const GET_USERS = gql`
  query GetUsers {
    users {
      id
      name
    }
  }
`;

const GET_USER = gql`
  query MyQuery($name: String!) {
    users(where: { name: { _eq: $name } }) {
      name
      id
    }
  }
`;

export default function Main({ name }) {
  const [todos, setTodos] = useState([]);
  const [users, setUsers] = useState([]);
  //   const { loading, error, data } = useQuery(GET_USERS);
  const { loading, error, data } = useQuery(GET_USER, {
    variables: { name: "alex" },
  });

  let navigate = useNavigate();

  useEffect(() => {
    console.log(todos);
  }, [todos]);

  useEffect(() => {
    if (data) {
      setUsers(data.users);
    }
  }, [data]);

  return (
    <div
      style={{
        width: "100vw",
        height: "100vh",
        display: "flex",
        flexDirection: "column",
        alignItems: "center",
        background: "linear-gradient(to right top,ghostwhite,floralwhite)",
        overflowY: "scroll",
        paddingBottom: "5%",
      }}
    >
      <Typography
        style={{
          fontSize: "4rem",
          fontFamily: "gillsans",
          marginTop: "10%",
          color: "crimson",
        }}
      >
        TODOS
      </Typography>
      <FormControl sx={{ m: 1, width: "20vw" }} variant="filled">
        <InputLabel htmlFor="filled-adornment-password">
          What needs to be done?
        </InputLabel>
        <FilledInput
          id="filled-adornment-password"
          onKeyDown={(e) => {
            if (e.key === "Enter") {
              console.log("enter", e.target.value);
              let temp = e.target.value;
              setTodos((p) => [...p, temp]);
              e.target.value = "";
            }
          }}
          endAdornment={
            <InputAdornment position="end">
              <IconButton
                aria-label="toggle password visibility"
                //   onClick={handleClickShowPassword}
                //   onMouseDown={handleMouseDownPassword}
                edge="end"
              >
                <KeyboardArrowDown />
              </IconButton>
            </InputAdornment>
          }
        />
      </FormControl>
      <Box
        sx={{
          display: "flex",
          flexDirection: "column",
        }}
      >
        <Paper
          elevation={3}
          style={{
            height: "auto",
            padding: "5%",
            width: "20vw",
            marginTop: "2%",
            overflowY: "scroll",
          }}
        >
          {todos.map((todo) => (
            <>
              <List
                style={{
                  width: "100%",
                  maxWidth: 360,
                  bgcolor: "background.paper",
                }}
              >
                <ListItemText variant="h5" component="div">
                  {todo}
                </ListItemText>
                <Divider />
              </List>
            </>
          ))}
        </Paper>
      </Box>
    </div>
  );
}
